INSERT INTO `job_recruit` VALUES ('17', '30', 'php ', 'php网站程序员', '3', '2016-05-14', '济南', '3000-5000', '1、一年以上网站开发经验，熟练掌握php进行网站开发，熟悉PHP开源框架，有大型网站开发经验的优先； \r\n2、熟悉MYSQL数据库开发、配置、维护、性能优化，熟练使用PHP+MYSQL数据库，并可以单独完成一定难度的项目； \r\n3、拥有良好的代码习惯，要求结构清晰，命名规范，逻辑性强； \r\n4、工作认真负责，严谨细致，良好的团队协作能力； \r\n\r\n\r\n岗位职责：\r\n1、负责公司各网站开发与测试，维护已有程序的升级和错误更正；\r\n2、参与项目开发，开发设计网站新功能，协同完成网站升级工作；\r\n3、配合网站编辑、客户服务等完成基于网站的各项工作；\r\n4、配合项目经理完成项目。\r\n5、待遇从优。\r\n', '张经理', '15726179927', '1', '2016-02-14 10:38:22', '1', '1', '请选择工作经验', '1000');
INSERT INTO `job_recruit` VALUES ('18', '30', '网站平面设计美工 ps photoshop', '网站平面设计美工', '3', '2016-05-14', '济南', '3000-5000', '网站美工招聘\r\n1、网站设计和运营维护中的设计工作；\r\n2、网站中的和页面显示效果有关的div+css设计\r\n3、能够完成网站整体的美术创意和页面规划设计。\r\n4、能够按照工作要求和时间要求，完成设计任务。\r\n\r\n相关工作经验及技能要求：\r\n1、精通Photoshop，Flash等软件的能力及网页制作流程； \r\n2、精通DIV+CSS、HTML、JAVASCRIPT等，熟悉W3C标准，能兼容各种浏览器； 懂网页程序优先；\r\n3、能够了解系统开发用户的需求，熟悉业务逻辑并根据用户需求建立友好的系统界面； \r\n4、熟悉网站建设的流程和网页设计制作流程，能独立完成网站页面制作，有成功的网页设计案例；', '张经理', '15726179927', '1', '2016-02-14 10:41:24', '1', '1', '请选择工作经验', '1000');
